                "thumbnail": "https://www.politico.eu/cdn-cgi/image/width=1200,height=630,fit=crop,quality=80,onerror=redirect/wp-content/uploads/2022/04/22/GettyImages-1240086378-scaled.jpg",
                "bias": "left-center",
